import { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from './Header';
import './App.css'

function App() {
  const [customerId, setCustomerId] = useState('');
  const [pdfUrl, setPdfUrl] = useState('');
  const [error, setError] = useState('');

  const generate = async (e) => {
    e.preventDefault();
    setError('');
    setPdfUrl('');

    try {
      const res = await axios.get(`http://localhost:8000/api/generate-statement/?customer_id=${customerId}`);
      if (res.data.success) {
        setPdfUrl(res.data.pdf_url);
      } else {
        setError(res.data.error);
      }
    } catch (err) {
      setError("Unable to reach backend server.");
    }
  };

  return (
    <>
    <Header />
      <div className="container mt-5">
      <h2 className="mb-4">Generate Credit Card Statement</h2>
      <form onSubmit={generate}>
        <div className="mb-3">
          <input
            type="number"
            className="form-control"
            placeholder="Enter Customer ID"
            value={customerId}
            onChange={(e) => setCustomerId(e.target.value)}
            required
          />
        </div>
        <button className="btn btn-primary">Generate</button>
      </form>

      {pdfUrl && (
        <div className="mt-3">
          <a className="btn btn-success" href={`http://localhost:8000${pdfUrl}`} download target="_blank">Download PDF</a>
        </div>
      )}
      {error && <div className="alert alert-danger mt-3">{error}</div>}
    </div>

    </>
  );
}

export default App;
