import { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from './Header';
import './App.css'
import Footer from './Footer';
import Content from './Content';

function App() {
  const [customerId, setCustomerId] = useState('');
  const [pdfUrl, setPdfUrl] = useState('');
  const [error, setError] = useState('');

  const generate = async (e) => {
    e.preventDefault();
    setError('');
    setPdfUrl('');

    try {
      const res = await axios.get(`http://localhost:8000/api/generate-statement/?customer_id=${customerId}`);
      if (res.data.success) {
        setPdfUrl(res.data.pdf_url);
      } else {
        setError(res.data.error);
      }
    } catch (err) {
      setError("Unable to reach backend server.");
    }
  };

  return (
    <>
    <Header />
    <Content customerId={customerId} setCustomerId={setCustomerId} pdfUrl={pdfUrl} error={error} generate={generate}/>
    <Footer />
    </>
  );
}

export default App;
